pragma solidity 0.8.28;

import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { AddressBook } from "../system/AddressBook.sol";
import { EventEmitter } from "../system/EventEmitter.sol";
import { PlatformToken } from "../platform/PlatformToken.sol";

/// @title DAO Staking Contract
/// @notice Manages Platform Token staking for governance voting power
/// @dev Allows users to stake Platform tokens to gain voting power in DAO governance
contract DaoStaking is UpgradeableContract, ReentrancyGuardUpgradeable {
    using SafeERC20 for PlatformToken;

    /// @notice Address book contract reference
    AddressBook public addressBook;

    /// @notice Platform token used for staking
    PlatformToken public platformToken;

    /// @notice Total amount of tokens staked in the contract
    uint256 public totalStaked;

    /// @notice Minimum staking period to prevent flash loan attacks (7 days)
    uint256 public constant MIN_STAKING_PERIOD = 7 days;

    /// @notice Mapping of user addresses to their staked token amounts
    mapping(address => uint256) public stakedAmount;

    /// @notice Mapping of user addresses to their staking timestamps
    mapping(address => uint256) public stakingTimestamp;

    constructor() UpgradeableContract() {}

    /// @notice Initializes the DaoStaking contract
    /// @param initialAddressBook Address of the AddressBook contract
    function initialize(address initialAddressBook) external initializer {
        require(initialAddressBook != address(0), "Invalid address book");
        
        addressBook = AddressBook(initialAddressBook);
        platformToken = addressBook.platformToken();

        __UpgradeableContract_init();
        __ReentrancyGuard_init();
    }

    /// @notice Stakes Platform tokens to gain voting power
    /// @param amount Amount of Platform tokens to stake
    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "Zero amount");
        require(platformToken.balanceOf(msg.sender) >= amount, "Insufficient balance");

        platformToken.safeTransferFrom(msg.sender, address(this), amount);
        
        stakedAmount[msg.sender] += amount;
        totalStaked += amount;
        stakingTimestamp[msg.sender] = block.timestamp;

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitDaoStaking_TokensStaked(
            msg.sender,
            amount,
            stakedAmount[msg.sender]
        );
    }

    /// @notice Unstakes Platform tokens after minimum staking period
    /// @param amount Amount of Platform tokens to unstake
    function unstake(uint256 amount) external nonReentrant {
        require(amount > 0, "Zero amount");
        require(stakedAmount[msg.sender] >= amount, "Insufficient staked amount");
        require(
            block.timestamp >= stakingTimestamp[msg.sender] + MIN_STAKING_PERIOD,
            "Minimum staking period not met"
        );

        stakedAmount[msg.sender] -= amount;
        totalStaked -= amount;

        // Reset staking timestamp if user has no more staked tokens
        if (stakedAmount[msg.sender] == 0) {
            stakingTimestamp[msg.sender] = 0;
        }

        platformToken.safeTransfer(msg.sender, amount);

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitDaoStaking_TokensUnstaked(
            msg.sender,
            amount,
            stakedAmount[msg.sender]
        );
    }

    /// @notice Gets the voting power for a specific user
    /// @param user Address of the user to check
    /// @return Voting power amount (equal to staked token amount)
    function getVotingPower(address user) external view returns (uint256) {
        return stakedAmount[user];
    }

    /// @notice Gets the total token supply for quorum calculations
    /// @dev This should be used for quorum calculations instead of just staked amount
    /// @return Total token supply
    function getTotalVotingPower() external view returns (uint256) {
        return platformToken.totalSupply();
    }

    /// @notice Checks if a user can unstake their tokens
    /// @param user Address of the user to check
    /// @return True if user can unstake, false otherwise
    function canUnstake(address user) external view returns (bool) {
        return block.timestamp >= stakingTimestamp[user] + MIN_STAKING_PERIOD;
    }

    /// @notice Gets the remaining time until a user can unstake
    /// @param user Address of the user to check
    /// @return Remaining time in seconds (0 if can already unstake)
    function getUnstakeTime(address user) external view returns (uint256) {
        uint256 unlockTime = stakingTimestamp[user] + MIN_STAKING_PERIOD;
        if (block.timestamp >= unlockTime) {
            return 0;
        }
        return unlockTime - block.timestamp;
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("DaoStaking");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireGovernance(msg.sender);
    }
}","keccak256":"0x1b3d94ca2b431fa0f39af51bc56e6f0c764103658556418db6d51670158e52c2","license":"MIT"},"contracts/dao/Governance.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { AddressBook } from "../system/AddressBook.sol";
import { Config } from "../system/Config.sol";
import { EventEmitter } from "../system/EventEmitter.sol";
import { DaoStaking } from "./DaoStaking.sol";

/// @title DAO Governance Contract
/// @notice Simple governance with instant proposals and automatic execution/cancellation
contract Governance is UpgradeableContract, ReentrancyGuardUpgradeable {
    AddressBook public addressBook;
    uint256 public proposalCount;

    struct Proposal {
        address proposer;
        address target;
        bytes data;
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 endTime;
        bool executed;
        bool cancelled;
    }

    struct Receipt {
        bool hasVoted;
        bool support;
        uint256 votes;
    }

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => Receipt)) public receipts;

    constructor() UpgradeableContract() {}

    function initialize(address initialAddressBook) external initializer {
        require(initialAddressBook != address(0), "Invalid address book");
        addressBook = AddressBook(initialAddressBook);
        __UpgradeableContract_init();
        __ReentrancyGuard_init();
    }

    /// @notice Creates proposal with instant start
    function propose(
        address target,
        bytes memory data,
        string memory description
    ) external returns (uint256 proposalId) {
        Config config = addressBook.config();
        DaoStaking daoStaking = addressBook.daoStaking();

        require(target != address(0), "Invalid target");
        require(bytes(description).length > 0, "Empty description");
        require(
            daoStaking.getVotingPower(msg.sender) >= config.proposalThreshold(),
            "Insufficient voting power"
        );

        proposalId = ++proposalCount;
        uint256 endTime = block.timestamp + config.votingPeriod();

        proposals[proposalId] = Proposal({
            proposer: msg.sender,
            target: target,
            data: data,
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            endTime: endTime,
            executed: false,
            cancelled: false
        });

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitGovernance_ProposalCreated(
            proposalId,
            msg.sender,
            target,
            data,
            description,
            block.timestamp,
            endTime
        );
    }

    /// @notice Vote and auto-execute/cancel
    function vote(uint256 proposalId, bool support, string memory reason) external nonReentrant {
        require(proposalId > 0 && proposalId <= proposalCount, "Invalid proposal");
        
        Proposal storage proposal = proposals[proposalId];
        require(!proposal.executed && !proposal.cancelled, "Proposal finished");
        require(block.timestamp <= proposal.endTime, "Voting ended");

        Receipt storage receipt = receipts[proposalId][msg.sender];
        require(!receipt.hasVoted, "Already voted");

        DaoStaking daoStaking = addressBook.daoStaking();
        uint256 votes = daoStaking.getVotingPower(msg.sender);
        require(votes > 0, "No voting power");

        // Record vote
        receipt.hasVoted = true;
        receipt.support = support;
        receipt.votes = votes;

        if (support) {
            proposal.votesFor += votes;
        } else {
            proposal.votesAgainst += votes;
        }

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitGovernance_VoteCast(proposalId, msg.sender, support, votes, reason);

        // Check for auto-execution or cancellation
        _checkAutoAction(proposalId);
    }

    /// @notice Manual cancel by proposer
    function cancel(uint256 proposalId) external {
        Proposal storage proposal = proposals[proposalId];
        require(
            msg.sender == proposal.proposer || msg.sender == address(this),
            "Not authorized"
        );
        require(!proposal.executed && !proposal.cancelled, "Proposal finished");

        _cancelProposal(proposalId, msg.sender);
    }

    /// @notice Get proposal info
    function getProposal(uint256 proposalId) external view returns (Proposal memory) {
        require(proposalId > 0 && proposalId <= proposalCount, "Invalid proposal");
        return proposals[proposalId];
    }

    /// @notice Get vote receipt
    function getReceipt(uint256 proposalId, address voter) external view returns (Receipt memory) {
        return receipts[proposalId][voter];
    }

    /// @notice Check if proposal is active
    function isActive(uint256 proposalId) external view returns (bool) {
        require(proposalId > 0 && proposalId <= proposalCount, "Invalid proposal");
        Proposal storage proposal = proposals[proposalId];
        return !proposal.executed && !proposal.cancelled && block.timestamp <= proposal.endTime;
    }

    /// @notice Private cancel logic (reusable)
    function _cancelProposal(uint256 proposalId, address canceller) private {
        proposals[proposalId].cancelled = true;
        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitGovernance_ProposalCancelled(proposalId, canceller);
    }

    /// @notice Check and execute auto-actions after vote
    function _checkAutoAction(uint256 proposalId) private {
        Proposal storage proposal = proposals[proposalId];

        // Skip if already finished
        if (proposal.executed || proposal.cancelled) {
            return;
        }

        Config config = addressBook.config();
        DaoStaking daoStaking = addressBook.daoStaking();
        
        uint256 totalVotingPower = daoStaking.getTotalVotingPower();
        uint256 quorum = (totalVotingPower * config.quorumPercentage()) / 10000;

        // Execute if enough votes FOR
        if (proposal.votesFor >= quorum && proposal.votesFor > proposal.votesAgainst) {
            (bool success, bytes memory returnData) = proposal.target.call(proposal.data);
            require(success, _getRevertMsg(returnData));

            proposal.executed = true;
            EventEmitter eventEmitter = addressBook.eventEmitter();
            eventEmitter.emitGovernance_ProposalExecuted(proposalId, address(this));
            return;
        }

        // Cancel if enough votes AGAINST
        if (proposal.votesAgainst >= quorum && proposal.votesAgainst > proposal.votesFor) {
            _cancelProposal(proposalId, address(this));
        }
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("Governance");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireTimelock(msg.sender);
    }

    function _getRevertMsg(bytes memory returnData) private pure returns (string memory) {
        if (returnData.length < 68) return "Transaction reverted silently";
        assembly {
            returnData := add(returnData, 0x04)
        }
        return abi.decode(returnData, (string));
    }
}
","keccak256":"0xf1a01cc7027079feba51e8b808eacc2c0725764afcdbf7c00dbcd23609467b52","license":"MIT"},"contracts/dao/Timelock.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { AddressBook } from "../system/AddressBook.sol";
import { Config } from "../system/Config.sol";
import { EventEmitter } from "../system/EventEmitter.sol";

/// @title DAO Timelock Contract
/// @notice Enforces delay on governance actions for security
/// @dev Implements timelock mechanism for governance proposals
contract Timelock is UpgradeableContract, ReentrancyGuardUpgradeable {
    /// @notice Address book contract reference
    AddressBook public addressBook;

    /// @notice Queued transactions
    mapping(bytes32 => uint256) public queuedTransactions;

    /// @notice Grace period for execution after ETA (7 days)
    uint256 public constant GRACE_PERIOD = 7 days;

    constructor() UpgradeableContract() {}

    /// @notice Initializes the contract
    /// @param initialAddressBook Address of AddressBook contract
    function initialize(address initialAddressBook) external initializer {
        require(initialAddressBook != address(0), "Invalid address book");
        
        addressBook = AddressBook(initialAddressBook);

        __UpgradeableContract_init();
        __ReentrancyGuard_init();
    }

    /// @notice Queues a transaction for future execution
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Earliest time for execution
    /// @return txHash Hash of the queued transaction
    function queueTransaction(
        address target,
        bytes memory data,
        uint256 eta
    ) external returns (bytes32 txHash) {
        addressBook.requireGovernance(msg.sender);
        
        Config config = addressBook.config();
        require(
            eta >= block.timestamp + config.timelockDelay(),
            "ETA too early"
        );

        txHash = keccak256(abi.encode(target, data, eta));
        require(queuedTransactions[txHash] == 0, "Transaction already queued");
        
        queuedTransactions[txHash] = eta;

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitTimelock_TransactionQueued(txHash, target, data, eta);

        return txHash;
    }

    /// @notice Executes a queued transaction
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Execution time
    function executeTransaction(
        address target,
        bytes memory data,
        uint256 eta
    ) external nonReentrant {
        bytes32 txHash = keccak256(abi.encode(target, data, eta));
        uint256 queuedEta = queuedTransactions[txHash];
        
        require(queuedEta != 0, "Transaction not queued");
        require(block.timestamp >= queuedEta, "Transaction not ready");
        require(block.timestamp <= queuedEta + GRACE_PERIOD, "Transaction expired");

        delete queuedTransactions[txHash];

        (bool success, bytes memory returnData) = target.call(data);
        require(success, _getRevertMsg(returnData));

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitTimelock_TransactionExecuted(txHash, target, data, eta);
    }

    /// @notice Cancels a queued transaction
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Execution time
    function cancelTransaction(
        address target,
        bytes memory data,
        uint256 eta
    ) external {
        addressBook.requireGovernance(msg.sender);
        
        bytes32 txHash = keccak256(abi.encode(target, data, eta));
        require(queuedTransactions[txHash] != 0, "Transaction not queued");

        delete queuedTransactions[txHash];

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitTimelock_TransactionCancelled(txHash, target, data, eta);
    }

    /// @notice Checks if a transaction is queued
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Execution time
    /// @return True if transaction is queued
    function isTransactionQueued(
        address target,
        bytes memory data,
        uint256 eta
    ) external view returns (bool) {
        bytes32 txHash = keccak256(abi.encode(target, data, eta));
        return queuedTransactions[txHash] != 0;
    }

    /// @notice Gets the ETA for a queued transaction
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Execution time
    /// @return The ETA timestamp
    function getTransactionETA(
        address target,
        bytes memory data,
        uint256 eta
    ) external view returns (uint256) {
        bytes32 txHash = keccak256(abi.encode(target, data, eta));
        return queuedTransactions[txHash];
    }

    /// @notice Checks if a transaction is ready for execution
    /// @param target Target contract address
    /// @param data Encoded function call data
    /// @param eta Execution time
    /// @return True if ready for execution
    function isTransactionReady(
        address target,
        bytes memory data,
        uint256 eta
    ) external view returns (bool) {
        bytes32 txHash = keccak256(abi.encode(target, data, eta));
        uint256 queuedEta = queuedTransactions[txHash];
        
        return queuedEta != 0 && 
               block.timestamp >= queuedEta && 
               block.timestamp <= queuedEta + GRACE_PERIOD;
    }

    /// @notice Extracts revert message from failed call
    /// @param returnData The return data from failed call
    /// @return Revert message string
    function _getRevertMsg(bytes memory returnData) internal pure returns (string memory) {
        if (returnData.length < 68) return "Transaction reverted silently";
        
        assembly {
            returnData := add(returnData, 0x04)
        }
        return abi.decode(returnData, (string));
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("Timelock");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireTimelock(msg.sender);
    }

    /// @notice Allows receiving ETH
    receive() external payable {}
}","keccak256":"0x3fec525a8891f4c9a1bcb4c4d74b4c2ee1f1431002f7ec8563df2f45ee84d4fd","license":"MIT"},"contracts/dao/Treasury.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { AddressBook } from "../system/AddressBook.sol";
import { EventEmitter } from "../system/EventEmitter.sol";

/// @title DAO Treasury Contract
/// @notice Manages DAO funds under timelock control
/// @dev Holds and manages treasury assets for the DAO
contract Treasury is UpgradeableContract {
    using SafeERC20 for IERC20;

    /// @notice Address book contract reference
    AddressBook public addressBook;
    

    constructor() UpgradeableContract() {}

    /// @notice Initializes the contract
    /// @param initialAddressBook Address of AddressBook contract
    function initialize(address initialAddressBook) external initializer {
        require(initialAddressBook != address(0), "Invalid address book");
        
        addressBook = AddressBook(initialAddressBook);

        __UpgradeableContract_init();
    }

    /// @notice Withdraws ERC20 tokens
    /// @param token Token address
    /// @param to Recipient address
    /// @param amount Amount to withdraw
    function withdrawERC20(
        address token,
        address to,
        uint256 amount
    ) external {
        addressBook.requireTimelock(msg.sender);
        require(to != address(0), "Zero address recipient");
        
        IERC20(token).safeTransfer(to, amount);

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitTreasury_Withdrawal(to, token, amount);
    }

    /// @notice Withdraws ETH
    /// @param to Recipient address
    /// @param amount Amount to withdraw
    function withdrawETH(address to, uint256 amount) external {
        addressBook.requireTimelock(msg.sender);
        require(to != address(0), "Zero address recipient");
        require(address(this).balance >= amount, "Insufficient ETH");
        
        (bool success, ) = to.call{value: amount}("");
        require(success, "ETH transfer failed");

        EventEmitter eventEmitter = addressBook.eventEmitter();
        eventEmitter.emitTreasury_Withdrawal(to, address(0), amount);
    }

    /// @notice Returns balance of specified ERC20 token
    /// @param token Token address
    /// @return Token balance
    function getTokenBalance(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("Treasury");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireGovernance(msg.sender);
    }

    /// @notice Allows receiving ETH
    receive() external payable {
    }
}
