pragma solidity 0.8.28;

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { ERC1155Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import { ERC1155SupplyUpgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { AddressBook } from "../system/AddressBook.sol";

/// @title RWA Token Contract
/// @notice Contract for managing real world asset tokens
/// @dev Implements ERC1155 standard with upgradeable functionality
contract RWA is UpgradeableContract, ERC1155Upgradeable, ERC1155SupplyUpgradeable {
    /// @notice Address book contract reference
    AddressBook public addressBook;

    /// @notice Emergency pause flag
    bool public paused;

    /// @notice Owner address
    address public owner;

    string public entityId;
    string public entityOwnerId;
    string public entityOwnerType;

    /// @notice Mapping of token ID to pool address
    mapping(uint256 => address) public pools;

    /// @notice Unique token ID amount
    uint256 public tokensLength;

    constructor() UpgradeableContract() {}

    /// @notice Initializes the contract
    /// @dev Can only be called once
    /// @param initialAddressBook Address of the AddressBook contract
    function initialize(
        address initialAddressBook,
        address deployer,
        address initialOwner,
        string memory initialEntityId,
        string memory initialEntityOwnerId,
        string memory initialEntityOwnerType
    ) external initializer {
        require(initialOwner != address(0), "Invalid owner");
        require(initialAddressBook != address(0), "Invalid addressBook");

        __UpgradeableContract_init();
        __ERC1155_init_unchained("");
        __ERC1155Supply_init_unchained();
        addressBook = AddressBook(initialAddressBook);
        owner = initialOwner;
        entityId = initialEntityId;
        entityOwnerId = initialEntityOwnerId;
        entityOwnerType = initialEntityOwnerType;

        addressBook.eventEmitter().emitRWA_Deployed(
            deployer,
            initialOwner,
            initialEntityId
        );
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("RWA");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireTimelock(msg.sender);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(UpgradeableContract, ERC1155Upgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /// @notice override base function
    function _update(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory values
    ) internal virtual override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) {
        require(!paused, "RWA: paused");
        super._update(from, to, ids, values);
        
        // Emit our custom event for each token transfer
        for (uint256 i = 0; i < ids.length; i++) {
            addressBook.eventEmitter().emitRWA_Transfer(from, to, ids[i], values[i], pools[ids[i]]);
        }
    }

    /// @notice Creates a new token with specified parameters
    /// @dev Can only be called by the factory address registered in AddressBook
    /// @param pool The address of the pool managing this token
    /// @return uint256 token id
    function createToken(address pool) external returns (uint256) {
        require(!paused, "RWA: paused");
        addressBook.requireFactory(msg.sender);
        uint256 tokenId = ++tokensLength;
        require(pools[tokenId] == address(0), "Token already exists");
        require(pool != address(0), "Invalid pool address");

        pools[tokenId] = pool;

        return tokenId;
    }

    /// @notice Mints tokens
    /// @dev Can only be called by the associated pool
    /// @param account The address to mint tokens to
    /// @param tokenId The ID of the token to mint
    /// @param amount The amount of tokens to mint
    function mint(address account, uint256 tokenId, uint256 amount) external {
        require(!paused, "RWA: paused");
        require(msg.sender == pools[tokenId], "Only pool can mint");
        require(account != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");

        _mint(account, tokenId, amount, "");
    }

    /// @notice Burns tokens
    /// @dev Can only be called by the associated pool
    /// @param account The address to burn tokens from
    /// @param tokenId The ID of the token to burn
    /// @param amount The amount of tokens to burn
    function burn(address account, uint256 tokenId, uint256 amount) external {
        require(!paused, "RWA: paused");
        require(msg.sender == pools[tokenId], "Only pool can burn");
        require(account != address(0), "Invalid account");
        require(amount > 0, "Amount must be greater than 0");
        require(balanceOf(account, tokenId) >= amount, "Insufficient balance");
        _burn(account, tokenId, amount);
    }

    /// @notice Gets the metadata URI for a specific token
    /// @param tokenId The ID of the token
    /// @return string The complete metadata URI for the token
    function uri(uint256 tokenId) public view override returns (string memory) {
        require(tokenId != 0 && tokenId <= tokensLength, "URI query for nonexistent token");

        string memory baseUri = addressBook.config().baseMetadataUri();
        return string(
            abi.encodePacked(
                baseUri,
                "/",
                Strings.toHexString(address(this)),
                "/",
                Strings.toString(tokenId)
            )
        );
    }

    /// @notice Enables emergency pause on the contract
    /// @dev Can only be called by governance. Contract operations will be blocked.
    function enablePause() external {
        addressBook.requireGovernance(msg.sender);
        require(!paused, "RWA: already paused");
        paused = true;
        addressBook.eventEmitter().emitRWA_PausedStateChanged(true);
    }

    /// @notice Disables emergency pause on the contract
    /// @dev Can only be called by governance. Contract operations will be unblocked.
    function disablePause() external {
        addressBook.requireTimelock(msg.sender);
        require(paused, "RWA: not paused");
        paused = false;
        addressBook.eventEmitter().emitRWA_PausedStateChanged(false);
    }
}
","keccak256":"0xa703377c7020a9c456097b97f226a6e254caeb17c075919f26daa567bd0341d2","license":"MIT"},"contracts/system/AddressBook.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";

import { Config } from "./Config.sol";
import { EventEmitter } from "./EventEmitter.sol";
import { DaoStaking } from "../dao/DaoStaking.sol";
import { Governance } from "../dao/Governance.sol";
import { Timelock } from "../dao/Timelock.sol";
import { Treasury } from "../dao/Treasury.sol";
import { PlatformToken } from "../platform/PlatformToken.sol";
import { ReferralTreasury } from "../platform/ReferralTreasury.sol";
import { Factory } from "../rwa/Factory.sol";
// import { Router } from "../rwa/Router.sol";
import { RWA } from "../rwa/RWA.sol";
import { Pool } from "../rwa/Pool.sol";

/// @title AddressBook contract for managing system addresses
/// @notice This contract stores and manages addresses of core protocol contracts
/// @dev Handles setting and managing addresses for all protocol contracts
contract AddressBook is UpgradeableContract {
    /// @notice The governance contract address
    Governance public governance;

    /// @notice The config contract address
    Config public config;

    /// @notice The eventEmitter contract address
    EventEmitter public eventEmitter;

    /// @notice The DAO staking contract address
    DaoStaking public daoStaking;

    /// @notice The timelock contract address
    Timelock public timelock;

    /// @notice The treasury contract address
    Treasury public treasury;

    /// @notice The platform token contract address
    PlatformToken public platformToken;

    /// @notice The referral treasury contract address
    ReferralTreasury public referralTreasury;

    /// @notice The factory contract address
    Factory public factory;

    // /// @notice The router contract address
    // Router public router;

    /// @notice The implementation contract address for RWA
    address public rwaImplementation;

    /// @notice The implementation contract address for Speculation Pool
    address public poolImplementation;

    /// @notice Array of all registered pools
    Pool[] internal pools;

    /// @notice Mapping to check if an address is a registered speculation pool
    mapping(address => bool) public isPool;

    /// @notice Array of all registered RWA addresses
    RWA[] internal rwas;

    /// @notice Mapping to check if an address is a registered RWA
    mapping(address => bool) public isRWA;

    /// @notice Mapping to check if an address is a registered protocol contract
    mapping(address => bool) public isProtocolContract;

    /// @notice Mapping to track authorized signers
    mapping(address => bool) public signers;

    /// @notice Number of authorized signers
    uint256 public signersLength;

    constructor() UpgradeableContract() {}

    /// @notice Initializes the contract setting initial governance
    /// @dev Can only be called once through initializer modifier
    function initialize() external initializer {
        __UpgradeableContract_init();
        governance = Governance(msg.sender);
        timelock = Timelock(payable(msg.sender));
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("AddressBook");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        requireTimelock(msg.sender);
    }

    /// @notice Checks if an address has governance rights
    /// @dev Reverts if account is not governance
    /// @param account The address to check
    function requireGovernance(address account) public view {
        require(account == address(governance), "AddressBook: not governance");
    }

    /// @notice Checks if an address has timelock rights
    /// @dev Reverts if account is not timelock
    /// @param account The address to check
    function requireTimelock(address account) public view {
        require(account == address(timelock), "Only timelock!");
    }

    /// @notice Checks if an address has factory rights
    /// @dev Reverts if account is not factory
    /// @param account The address to check
    function requireFactory(address account) public view {
        require(account == address(factory), "Only factory!");
    }

    /// @notice Updates the governance address
    /// @dev Can only be called by current governance
    /// @param newGovernance The address of the new governance
    function setGovernance(Governance newGovernance) external {
        requireGovernance(msg.sender);
        if (address(governance) != address(0)) {
            isProtocolContract[address(governance)] = false;
        }
        governance = newGovernance;
        isProtocolContract[address(newGovernance)] = true;
    }

    /// @notice Sets the config contract address
    /// @dev Can only be called by governance
    /// @param newConfig The address of the new config contract
    function setConfig(Config newConfig) external {
        requireGovernance(msg.sender);
        if (address(config) != address(0)) {
            isProtocolContract[address(config)] = false;
        }
        config = newConfig;
        isProtocolContract[address(newConfig)] = true;
    }

    /// @notice Sets the DAO staking contract address
    /// @dev Can only be called by governance
    /// @param newDaoStaking The address of the new DAO staking contract
    function setDaoStaking(DaoStaking newDaoStaking) external {
        requireGovernance(msg.sender);
        if (address(daoStaking) != address(0)) {
            isProtocolContract[address(daoStaking)] = false;
        }
        daoStaking = newDaoStaking;
        isProtocolContract[address(newDaoStaking)] = true;
    }

    /// @notice Sets the timelock contract address
    /// @dev Can only be called by governance
    /// @param newTimelock The address of the new timelock contract
    function setTimelock(Timelock newTimelock) external {
        requireGovernance(msg.sender);
        if (address(timelock) != address(0)) {
            isProtocolContract[address(timelock)] = false;
        }
        timelock = newTimelock;
        isProtocolContract[address(newTimelock)] = true;
    }

    /// @notice Sets the treasury contract address
    /// @dev Can only be called by governance
    /// @param newTreasury The address of the new treasury contract
    function setTreasury(Treasury newTreasury) external {
        requireGovernance(msg.sender);
        if (address(treasury) != address(0)) {
            isProtocolContract[address(treasury)] = false;
        }
        treasury = newTreasury;
        isProtocolContract[address(newTreasury)] = true;
    }

    /// @notice Sets the platform token contract address
    /// @dev Can only be called by governance
    /// @param newPlatformToken The address of the new platform token contract
    function setPlatformToken(PlatformToken newPlatformToken) external {
        requireGovernance(msg.sender);
        if (address(platformToken) != address(0)) {
            isProtocolContract[address(platformToken)] = false;
        }
        platformToken = newPlatformToken;
        isProtocolContract[address(newPlatformToken)] = true;
    }

    /// @notice Sets the referral treasury contract address
    /// @dev Can only be called by governance
    /// @param newReferralTreasury The address of the new referral treasury contract
    function setReferralTreasury(ReferralTreasury newReferralTreasury) external {
        requireGovernance(msg.sender);
        if (address(referralTreasury) != address(0)) {
            isProtocolContract[address(referralTreasury)] = false;
        }
        referralTreasury = newReferralTreasury;
        isProtocolContract[address(newReferralTreasury)] = true;
    }

    /// @notice Sets the factory contract address
    /// @dev Can only be called by governance
    /// @param newFactory The address of the new factory contract
    function setFactory(Factory newFactory) external {
        requireGovernance(msg.sender);
        if (address(factory) != address(0)) {
            isProtocolContract[address(factory)] = false;
        }
        factory = newFactory;
        isProtocolContract[address(newFactory)] = true;
    }

    /// @notice Adds a new signer
    /// @dev Can only be called by governance
    /// @param newSigner The address of the new signer to add
    function addSigner(address newSigner) external {
        requireGovernance(msg.sender);
        require(newSigner != address(0), "Invalid signer address");
        require(!signers[newSigner], "Signer already exists");
        
        signers[newSigner] = true;
        signersLength++;
    }

    /// @notice Removes an existing signer
    /// @dev Can only be called by governance
    /// @param signer The address of the signer to remove
    function removeSigner(address signer) external {
        requireGovernance(msg.sender);
        require(signers[signer], "Signer does not exist");
        
        signers[signer] = false;
        signersLength--;
    }

    /// @notice Checks if an address is an authorized signer
    /// @dev Reverts if account is not a signer
    /// @param account The address to check
    function requireSigner(address account) public view {
        require(signers[account], "Not an authorized signer!");
    }

    /// @notice Sets the event emitter contract address
    /// @dev Can only be called by governance
    /// @param newEventEmitter The address of the new event emitter contract
    function setEventEmitter(EventEmitter newEventEmitter) external {
        requireGovernance(msg.sender);
        eventEmitter = newEventEmitter;
    }

    /// @notice Sets the RWA implementation contract address
    /// @dev Can only be called by governance
    /// @param newImplementation The address of the new RWA implementation
    function setRWAImplementation(address newImplementation) external {
        requireGovernance(msg.sender);
        rwaImplementation = newImplementation;
    }

    /// @notice Sets the Speculation Pool implementation contract address
    /// @dev Can only be called by governance
    /// @param newImplementation The address of the new Speculation Pool implementation
    function setPoolImplementation(address newImplementation) external {
        requireGovernance(msg.sender);
        poolImplementation = newImplementation;
    }

    /// @notice Checks if an address is a registered protocol contract
    /// @dev Reverts if account is not a protocol contract
    /// @param account The address to check
    function requireProtocolContract(address account) public view {
        require(isProtocolContract[account], "Not a protocol contract!");
    }

    /// @notice Adds a new  pool to the system
    /// @dev Can only be called by factory
    /// @param pool The address of the pool to add
    function addPool(Pool pool) external {
        requireFactory(msg.sender);
        require(!isPool[address(pool)], "Pool already exists");
        pools.push(pool);
        isPool[address(pool)] = true;
        isProtocolContract[address(pool)] = true;
    }

    /// @notice Adds a new RWA to the system
    /// @dev Can only be called by governance
    /// @param rwa The address of the RWA to add
    function addRWA(RWA rwa) external {
        require(msg.sender == address(factory), "Only factory!");
        require(!isRWA[address(rwa)], "RWA already exists");
        rwas.push(rwa);
        isRWA[address(rwa)] = true;
        isProtocolContract[address(rwa)] = true;
    }

    /// @notice Returns pool at specific index
    /// @param index Index of the pool to return
    /// @return BasePool Pool at specified index
    function getPoolByIndex(uint256 index) external view returns(Pool) {
        require(index < pools.length, "Index out of bounds");
        return pools[index];
    }

    /// @notice Returns RWA at specific index
    /// @param index Index of the RWA to return
    /// @return RWA RWA at specified index
    function getRWAByIndex(uint256 index) external view returns(RWA) {
        require(index < rwas.length, "Index out of bounds");
        return rwas[index];
    }

    /// @notice Returns the number of registered RWAs
    /// @return uint256 Number of registered RWAs
    function rwasLength() external view returns(uint256) {
        return rwas.length;
    }

    /// @notice Returns the number of registered pools
    /// @return uint256 Number of registered pools
    function poolsLength() external view returns(uint256) {
        return pools.length;
    }
}
","keccak256":"0x67d9d360ea3fb5cd2c268b8b52274341b996509f139a4e2ed8599888d5eba225","license":"MIT"},"contracts/system/Config.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { AddressBook } from "./AddressBook.sol";

/// @title Configuration contract for RWA protocol
/// @notice Stores all configurable parameters for the protocol
/// @dev Upgradeable contract using UUPS proxy pattern
contract Config is UpgradeableContract {
    /// @notice Address book contract reference
    AddressBook public addressBook;
    
    /// @notice Base URI for token metadata
    string public baseMetadataUri;

    /// @notice Token used for holding
    IERC20 public holdToken;

    /// @notice Minimum number of signers required for operations
    uint256 public minSignersRequired;

    /// @notice Fee range for creating RWA (in HOLD tokens)
    uint256 public createRWAFeeMin;
    uint256 public createRWAFeeMax;

    /// @notice Fee ratio range for creating pool (in basis points, max 10000 = 100%)
    uint256 public createPoolFeeRatioMin;
    uint256 public createPoolFeeRatioMax;

    /// @notice Expected HOLD amount range
    uint256 public expectedHoldAmountMin;
    uint256 public expectedHoldAmountMax;

    /// @notice Expected RWA amount range
    uint256 public expectedRwaAmountMin;
    uint256 public expectedRwaAmountMax;

    /// @notice Entry fee percentage range (in basis points, max 10000 = 100%)
    uint256 public entryFeePercentMin;
    uint256 public entryFeePercentMax;

    /// @notice Exit fee percentage range (in basis points, max 10000 = 100%)
    uint256 public exitFeePercentMin;
    uint256 public exitFeePercentMax;

    /// @notice Reward percentage range (in basis points)
    uint256 public rewardPercentMin;
    uint256 public rewardPercentMax;

    // --- Period Configuration ---
    /// @notice Minimum duration for entry period (between start and expired)
    uint256 public entryPeriodMinDuration;
    
    /// @notice Maximum duration for entry period (between start and expired)
    uint256 public entryPeriodMaxDuration;

    /// @notice Minimum duration for completion period (between entry expired and completion expired)
    uint256 public completionPeriodMinDuration;
    
    /// @notice Maximum duration for completion period (between entry expired and completion expired)
    uint256 public completionPeriodMaxDuration;

    /// @notice Maximum past offset for entry period start (e.g. 1 days for yesterday)
    uint256 public maxEntryStartPastOffset;
    
    /// @notice Maximum future offset for entry period start (e.g. 180 days for 6 months)
    uint256 public maxEntryStartFutureOffset;

    // --- Tranche Configuration ---
    /// @notice Configuration for outgoing tranches
    uint256 public outgoingTranchesMinCount;
    uint256 public outgoingTranchesMaxCount;
    uint256 public outgoingTranchesMinPercent;
    uint256 public outgoingTranchesMaxPercent;
    uint256 public outgoingTranchesMinInterval;

    /// @notice Configuration for incoming tranches
    uint256 public incomingTranchesMinCount;
    uint256 public incomingTranchesMaxCount;
    uint256 public incomingTranchesMinPercent;
    uint256 public incomingTranchesMaxPercent;
    uint256 public incomingTranchesMinInterval;

    // --- DAO Governance Configuration ---
    /// @notice Voting period duration in seconds
    uint256 public votingPeriod;
    
    /// @notice Minimum voting delay before proposal can be executed (in seconds)
    uint256 public votingDelay;
    
    /// @notice Quorum percentage required for proposal to pass (in basis points)
    uint256 public quorumPercentage;
    
    /// @notice Proposal threshold - minimum tokens needed to create proposal
    uint256 public proposalThreshold;
    
    /// @notice Timelock delay for executing proposals (in seconds)
    uint256 public timelockDelay;

    // --- Liquidity Coefficient Configuration ---
    /// @notice Mapping of price impact percentage (multiplied by 100) to liquidity coefficient
    /// @dev Example: 1 => 13334 means 0.01% => 13334
    mapping(uint256 => uint256) public liquidityCoefficients;

    /// @notice Constructor that disables initializers
    constructor() UpgradeableContract() {}

    function initialize(
        address initialAddressBook,
        string memory initialBaseMetadataUri,
        address initialHoldToken,
        uint256 initialMinSignersRequired,
        uint256 initialCreateRWAFeeMin,
        uint256 initialCreateRWAFeeMax,
        uint256 initialCreatePoolFeeRatioMin,
        uint256 initialCreatePoolFeeRatioMax,
        uint256 initialExpectedHoldAmountMin,
        uint256 initialExpectedHoldAmountMax,
        uint256 initialExpectedRwaAmountMin,
        uint256 initialExpectedRwaAmountMax,
        uint256 initialEntryFeePercentMin,
        uint256 initialEntryFeePercentMax,
        uint256 initialExitFeePercentMin,
        uint256 initialExitFeePercentMax,
        uint256 initialRewardPercentMin,
        uint256 initialRewardPercentMax,
        uint256 initialEntryPeriodMinDuration,
        uint256 initialEntryPeriodMaxDuration,
        uint256 initialCompletionPeriodMinDuration,
        uint256 initialCompletionPeriodMaxDuration,
        uint256 initialMaxEntryStartPastOffset,
        uint256 initialMaxEntryStartFutureOffset,
        uint256 initialOutgoingTranchesMinCount,
        uint256 initialOutgoingTranchesMaxCount,
        uint256 initialOutgoingTranchesMinPercent,
        uint256 initialOutgoingTranchesMaxPercent,
        uint256 initialOutgoingTranchesMinInterval,
        uint256 initialIncomingTranchesMinCount,
        uint256 initialIncomingTranchesMaxCount,
        uint256 initialIncomingTranchesMinPercent,
        uint256 initialIncomingTranchesMaxPercent,
        uint256 initialIncomingTranchesMinInterval,
        uint256 initialVotingPeriod,
        uint256 initialVotingDelay,
        uint256 initialQuorumPercentage,
        uint256 initialProposalThreshold,
        uint256 initialTimelockDelay,
        uint256[] memory initialPriceImpactPercentages,
        uint256[] memory initialCoefficients
    ) external initializer {
        __UpgradeableContract_init();

        require(initialAddressBook != address(0), "Invalid address book");
        require(initialHoldToken != address(0), "Invalid hold token");
        require(initialMinSignersRequired > 0, "Invalid min signers required");
        require(initialCreateRWAFeeMin < initialCreateRWAFeeMax, "Invalid RWA fee range");
        require(initialCreatePoolFeeRatioMin < initialCreatePoolFeeRatioMax, "Invalid pool fee ratio range");
        require(initialCreatePoolFeeRatioMax <= 10000, "Pool fee ratio too high"); // Max 100%

        // Validate amount ranges
        require(initialExpectedHoldAmountMin < initialExpectedHoldAmountMax, "Invalid expected HOLD range");
        require(initialExpectedRwaAmountMin < initialExpectedRwaAmountMax, "Invalid expected RWA range");
        require(initialEntryFeePercentMax <= 10000, "Entry fee too high"); // Max 100%
        require(initialExitFeePercentMax <= 10000, "Exit fee too high"); // Max 100%
        require(initialRewardPercentMin < initialRewardPercentMax, "Invalid reward range");

        // Validate period durations
        require(initialEntryPeriodMinDuration > 0, "Invalid entry period min duration");
        require(initialEntryPeriodMaxDuration > initialEntryPeriodMinDuration, "Invalid entry period max duration");
        require(initialCompletionPeriodMinDuration > 0, "Invalid completion period min duration");
        require(initialCompletionPeriodMaxDuration > initialCompletionPeriodMinDuration, "Invalid completion period max duration");

        // Validate entry period offsets
        require(initialMaxEntryStartPastOffset > 0, "Invalid past offset");
        require(initialMaxEntryStartFutureOffset > initialMaxEntryStartPastOffset, "Invalid future offset");

        // Validate tranche configs
        require(initialOutgoingTranchesMinCount > 0 && initialOutgoingTranchesMaxCount >= initialOutgoingTranchesMinCount, "Invalid outgoing count range");
        require(initialOutgoingTranchesMinPercent > 0 && initialOutgoingTranchesMaxPercent <= 10000, "Invalid outgoing percent range");
        require(initialOutgoingTranchesMinInterval > 0, "Invalid outgoing interval");

        require(initialIncomingTranchesMinCount > 0 && initialIncomingTranchesMaxCount >= initialIncomingTranchesMinCount, "Invalid incoming count range");
        require(initialIncomingTranchesMinPercent > 0 && initialIncomingTranchesMaxPercent <= 10000, "Invalid incoming percent range");
        require(initialIncomingTranchesMinInterval > 0, "Invalid incoming interval");

        addressBook = AddressBook(initialAddressBook);
        baseMetadataUri = initialBaseMetadataUri;
        holdToken = IERC20(initialHoldToken);
        minSignersRequired = initialMinSignersRequired;

        createRWAFeeMin = initialCreateRWAFeeMin;
        createRWAFeeMax = initialCreateRWAFeeMax;
        createPoolFeeRatioMin = initialCreatePoolFeeRatioMin;
        createPoolFeeRatioMax = initialCreatePoolFeeRatioMax;

        expectedHoldAmountMin = initialExpectedHoldAmountMin;
        expectedHoldAmountMax = initialExpectedHoldAmountMax;
        expectedRwaAmountMin = initialExpectedRwaAmountMin;
        expectedRwaAmountMax = initialExpectedRwaAmountMax;

        entryFeePercentMin = initialEntryFeePercentMin;
        entryFeePercentMax = initialEntryFeePercentMax;
        exitFeePercentMin = initialExitFeePercentMin;
        exitFeePercentMax = initialExitFeePercentMax;

        rewardPercentMin = initialRewardPercentMin;
        rewardPercentMax = initialRewardPercentMax;

        entryPeriodMinDuration = initialEntryPeriodMinDuration;
        entryPeriodMaxDuration = initialEntryPeriodMaxDuration;
        completionPeriodMinDuration = initialCompletionPeriodMinDuration;
        completionPeriodMaxDuration = initialCompletionPeriodMaxDuration;
        maxEntryStartPastOffset = initialMaxEntryStartPastOffset;
        maxEntryStartFutureOffset = initialMaxEntryStartFutureOffset;

        outgoingTranchesMinCount = initialOutgoingTranchesMinCount;
        outgoingTranchesMaxCount = initialOutgoingTranchesMaxCount;
        outgoingTranchesMinPercent = initialOutgoingTranchesMinPercent;
        outgoingTranchesMaxPercent = initialOutgoingTranchesMaxPercent;
        outgoingTranchesMinInterval = initialOutgoingTranchesMinInterval;

        incomingTranchesMinCount = initialIncomingTranchesMinCount;
        incomingTranchesMaxCount = initialIncomingTranchesMaxCount;
        incomingTranchesMinPercent = initialIncomingTranchesMinPercent;
        incomingTranchesMaxPercent = initialIncomingTranchesMaxPercent;
        incomingTranchesMinInterval = initialIncomingTranchesMinInterval;

        // Set DAO governance parameters
        votingPeriod = initialVotingPeriod;
        votingDelay = initialVotingDelay;
        quorumPercentage = initialQuorumPercentage;
        proposalThreshold = initialProposalThreshold;
        timelockDelay = initialTimelockDelay;

        // Set initial liquidity coefficients
        require(initialPriceImpactPercentages.length == initialCoefficients.length, "Arrays length mismatch");
        require(initialPriceImpactPercentages.length > 0, "Empty arrays");

        for (uint256 i = 0; i < initialPriceImpactPercentages.length; i++) {
            require(initialCoefficients[i] > 0, "Invalid coefficient");
            liquidityCoefficients[initialPriceImpactPercentages[i]] = initialCoefficients[i];
        }
    }

    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("Config");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireTimelock(msg.sender);
    }

    /// @notice Updates base metadata URI
    /// @param newBaseMetadataUri New base URI for metadata
    function updateBaseMetadataUri(string memory newBaseMetadataUri) external {
        addressBook.requireGovernance(msg.sender);
        baseMetadataUri = newBaseMetadataUri;
    }

    /// @notice Updates hold token address
    /// @param newHoldToken New hold token address
    function updateHoldToken(address newHoldToken) external {
        addressBook.requireGovernance(msg.sender);
        require(newHoldToken != address(0), "Invalid hold token");
        holdToken = IERC20(newHoldToken);
    }

    /// @notice Updates minimum required number of signers
    /// @param newMinSignersRequired New minimum number of signers required
    function updateMinSignersRequired(uint256 newMinSignersRequired) external {
        addressBook.requireGovernance(msg.sender);
        require(newMinSignersRequired > 0, "Invalid min signers");
        minSignersRequired = newMinSignersRequired;
    }

    /// @notice Updates RWA creation fee range
    /// @param newMin New minimum fee
    /// @param newMax New maximum fee
    function updateCreateRWAFeeRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid RWA fee range");
        createRWAFeeMin = newMin;
        createRWAFeeMax = newMax;
    }

    /// @notice Updates pool creation fee ratio range
    /// @param newMin New minimum ratio
    /// @param newMax New maximum ratio
    function updateCreatePoolFeeRatioRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid pool fee ratio range");
        require(newMax <= 10000, "Pool fee ratio too high"); // Max 100%
        createPoolFeeRatioMin = newMin;
        createPoolFeeRatioMax = newMax;
    }

    /// @notice Updates expected HOLD amount range
    /// @param newMin New minimum amount
    /// @param newMax New maximum amount
    function updateExpectedHoldAmountRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid HOLD range");
        expectedHoldAmountMin = newMin;
        expectedHoldAmountMax = newMax;
    }

    /// @notice Updates expected RWA amount range
    /// @param newMin New minimum amount
    /// @param newMax New maximum amount
    function updateExpectedRwaAmountRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid RWA range");
        expectedRwaAmountMin = newMin;
        expectedRwaAmountMax = newMax;
    }

    /// @notice Updates entry fee percentage range
    /// @param newMin New minimum percentage
    /// @param newMax New maximum percentage
    function updateEntryFeePercentRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid entry fee range");
        require(newMax <= 10000, "Entry fee too high"); // Max 100%
        entryFeePercentMin = newMin;
        entryFeePercentMax = newMax;
    }

    /// @notice Updates exit fee percentage range
    /// @param newMin New minimum percentage
    /// @param newMax New maximum percentage
    function updateExitFeePercentRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid exit fee range");
        require(newMax <= 10000, "Exit fee too high"); // Max 100%
        exitFeePercentMin = newMin;
        exitFeePercentMax = newMax;
    }

    /// @notice Updates reward percentage range
    /// @param newMin New minimum percentage
    /// @param newMax New maximum percentage
    function updateRewardPercentRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin < newMax, "Invalid reward range");
        rewardPercentMin = newMin;
        rewardPercentMax = newMax;
    }

    /// @notice Updates entry period configuration
    /// @param newMaxPastOffset New maximum past offset for entry start
    /// @param newMaxFutureOffset New maximum future offset for entry start
    function updateEntryPeriodConfig(uint256 newMaxPastOffset, uint256 newMaxFutureOffset) external {
        addressBook.requireGovernance(msg.sender);
        require(newMaxPastOffset > 0, "Invalid past offset");
        require(newMaxFutureOffset > newMaxPastOffset, "Invalid future offset");
        maxEntryStartPastOffset = newMaxPastOffset;
        maxEntryStartFutureOffset = newMaxFutureOffset;
    }

    /// @notice Updates entry period duration range
    /// @param newMin New minimum duration
    /// @param newMax New maximum duration
    function updateEntryPeriodDurationRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin > 0, "Invalid entry period min duration");
        require(newMax > newMin, "Invalid entry period max duration");
        entryPeriodMinDuration = newMin;
        entryPeriodMaxDuration = newMax;
    }

    /// @notice Updates completion period duration range
    /// @param newMin New minimum duration
    /// @param newMax New maximum duration
    function updateCompletionPeriodDurationRange(uint256 newMin, uint256 newMax) external {
        addressBook.requireGovernance(msg.sender);
        require(newMin > 0, "Invalid completion period min duration");
        require(newMax > newMin, "Invalid completion period max duration");
        completionPeriodMinDuration = newMin;
        completionPeriodMaxDuration = newMax;
    }

    /// @notice Updates outgoing tranches configuration
    function updateOutgoingTranchesConfig(
        uint256 newMinCount,
        uint256 newMaxCount,
        uint256 newMinPercent,
        uint256 newMaxPercent,
        uint256 newMinInterval
    ) external {
        addressBook.requireGovernance(msg.sender);
        require(newMinCount > 0 && newMaxCount >= newMinCount, "Invalid count range");
        require(newMinPercent > 0 && newMaxPercent <= 10000, "Invalid percent range");
        require(newMinInterval > 0, "Invalid interval");

        outgoingTranchesMinCount = newMinCount;
        outgoingTranchesMaxCount = newMaxCount;
        outgoingTranchesMinPercent = newMinPercent;
        outgoingTranchesMaxPercent = newMaxPercent;
        outgoingTranchesMinInterval = newMinInterval;
    }

    /// @notice Updates incoming tranches configuration
    function updateIncomingTranchesConfig(
        uint256 newMinCount,
        uint256 newMaxCount,
        uint256 newMinPercent,
        uint256 newMaxPercent,
        uint256 newMinInterval
    ) external {
        addressBook.requireGovernance(msg.sender);
        require(newMinCount > 0 && newMaxCount >= newMinCount, "Invalid count range");
        require(newMinPercent > 0 && newMaxPercent <= 10000, "Invalid percent range");
        require(newMinInterval > 0, "Invalid interval");

        incomingTranchesMinCount = newMinCount;
        incomingTranchesMaxCount = newMaxCount;
        incomingTranchesMinPercent = newMinPercent;
        incomingTranchesMaxPercent = newMaxPercent;
        incomingTranchesMinInterval = newMinInterval;
    }

    /// @notice Updates liquidity coefficients mapping
    /// @param percentages Array of price impact percentages (multiplied by 100)
    /// @param coefficients Array of corresponding liquidity coefficients
    function updateLiquidityCoefficients(
        uint256[] memory percentages,
        uint256[] memory coefficients
    ) external {
        addressBook.requireGovernance(msg.sender);
        require(percentages.length == coefficients.length, "Arrays length mismatch");
        require(percentages.length > 0, "Empty arrays");

        // Set new coefficients
        for (uint256 i = 0; i < percentages.length; i++) {
            require(coefficients[i] > 0, "Invalid coefficient");
            liquidityCoefficients[percentages[i]] = coefficients[i];
        }
    }

    /// @notice Updates DAO governance voting period
    /// @param newVotingPeriod New voting period in seconds
    function updateVotingPeriod(uint256 newVotingPeriod) external {
        addressBook.requireGovernance(msg.sender);
        require(newVotingPeriod > 0, "Invalid voting period");
        votingPeriod = newVotingPeriod;
    }

    /// @notice Updates DAO governance voting delay
    /// @param newVotingDelay New voting delay in seconds
    function updateVotingDelay(uint256 newVotingDelay) external {
        addressBook.requireGovernance(msg.sender);
        votingDelay = newVotingDelay;
    }

    /// @notice Updates DAO governance quorum percentage
    /// @param newQuorumPercentage New quorum percentage in basis points
    function updateQuorumPercentage(uint256 newQuorumPercentage) external {
        addressBook.requireGovernance(msg.sender);
        require(newQuorumPercentage > 0 && newQuorumPercentage <= 10000, "Invalid quorum percentage");
        quorumPercentage = newQuorumPercentage;
    }

    /// @notice Updates DAO governance proposal threshold
    /// @param newProposalThreshold New proposal threshold amount
    function updateProposalThreshold(uint256 newProposalThreshold) external {
        addressBook.requireGovernance(msg.sender);
        require(newProposalThreshold > 0, "Invalid proposal threshold");
        proposalThreshold = newProposalThreshold;
    }

    /// @notice Updates DAO timelock delay
    /// @param newTimelockDelay New timelock delay in seconds
    function updateTimelockDelay(uint256 newTimelockDelay) external {
        addressBook.requireGovernance(msg.sender);
        require(newTimelockDelay > 0, "Invalid timelock delay");
        timelockDelay = newTimelockDelay;
    }

    /// @notice Gets liquidity coefficient for a given price impact percentage
    /// @param percentage Price impact percentage (multiplied by 100)
    /// @return coefficient Corresponding liquidity coefficient
    function getLiquidityCoefficient(uint256 percentage) external view returns (uint256 coefficient) {
        coefficient = liquidityCoefficients[percentage];
        require(coefficient > 0, "Coefficient not found");
        return coefficient;
    }
}
","keccak256":"0x5dd45c7e889d420312733f9a848f0c9b30e69df9aeba7fc7263b5b7ef6c751e2","license":"MIT"},"contracts/system/EventEmitter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { UpgradeableContract } from "../utils/UpgradeableContract.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { AddressBook } from "./AddressBook.sol";

contract EventEmitter is UpgradeableContract {
    /// @notice Address book contract reference
    AddressBook public addressBook;
    
    uint256 public genesisBlock;

    constructor() UpgradeableContract() {}

    function initialize(address initialAddressBook) external initializer {
        __UpgradeableContract_init();

        require(initialAddressBook != address(0), "Invalid address book");
        addressBook = AddressBook(initialAddressBook);
        genesisBlock = block.number;
    }

    // --- Pool Events Start ---

    event Pool_OutgoingTrancheClaimed(
        address indexed emittedFrom,
        address indexed claimer,
        uint256 trancheIndex,
        uint256 amountClaimed,
        address holdToken
    );

    event Pool_OutgoingClaimSummary(
        address indexed emittedFrom,
        uint256 currentTotalClaimedAmount,
        uint256 currentOutgoingTranchesBalance
    );

    event Pool_IncomingTrancheUpdate(
        address indexed emittedFrom,
        address indexed caller,
        address owner,
        uint256 trancheIndex,
        uint256 amountAppliedToTranche,
        bool isNowComplete,
        bool wasOnTime,
        address holdToken
    );

    event Pool_IncomingReturnSummary(
        address indexed emittedFrom,
        uint256 currentTotalReturnedAmount,
        uint256 currentAwaitingBonusAmount,
        uint256 currentLastCompletedIncomingTranche
    );

    event Pool_BonusWithdrawn(
        address indexed emittedFrom,
        uint256 currentAwaitingBonusAmount,
        uint256 currentRewardedRwaAmount
    );

    event Pool_FundsFullyReturned(
        address indexed emittedFrom,
        address indexed caller,
        address owner,
        uint256 timestamp
    );

    event Pool_RwaMinted(
        address indexed emittedFrom,
        address indexed minter,
        uint256 rwaAmountMinted,
        uint256 holdAmountPaid,
        uint256 feePaid,
        uint256 percentBefore,
        uint256 userPercent,
        bool targetReached,
        string businessId,
        string poolId,
        address holdToken
    );

    event Pool_TargetReached(
        address indexed emittedFrom,
        uint256 outgoingTranchesBalance,
        uint256 floatingTimestampOffset,
        address owner
    );

    event Pool_RwaBurned(
        address indexed emittedFrom,
        address indexed burner,
        uint256 rwaAmountBurned,
        uint256 holdAmountReceived,
        uint256 bonusAmountReceived,
        uint256 holdFeePaid,
        uint256 bonusFeePaid,
        uint256 percentBefore,
        uint256 userPercent,
        bool targetReached,
        string businessId,
        string poolId,
        address holdToken
    );

    event Pool_AwaitingRwaAmountUpdated(
        address indexed emittedFrom, 
        uint256 awaitingRwaAmount
    );

    event Pool_ReservesUpdated(
        address indexed emittedFrom, 
        uint256 realHoldReserve,
        uint256 virtualHoldReserve,
        uint256 virtualRwaReserve
    );

    event Pool_PausedStateChanged(
        address indexed emittedFrom, 
        bool isPaused
    );

    event Pool_Deployed(
        address indexed emittedFrom,
        address indexed deployer,
        bool awaitCompletionExpired,
        bool floatingOutTranchesTimestamps,
        address holdToken,
        address rwaToken,
        uint256 tokenId,
        string entityId,
        string entityOwnerId,
        string entityOwnerType,
        address owner,
        uint256 expectedHoldAmount,
        uint256 expectedRwaAmount,
        uint256 expectedBonusAmount,
        uint256 rewardPercent,
        bool fixedSell,
        bool allowEntryBurn,
        uint256 entryPeriodStart,
        uint256 entryPeriodExpired,
        uint256 completionPeriodExpired,
        uint256 k,
        uint256 entryFeePercent,
        uint256 exitFeePercent,
        uint256[] outgoingTranches,
        uint256[] outgoingTranchTimestamps,
        uint256[] incomingTranches,
        uint256[] incomingTrancheExpired
    );

    // --- Pool Emitter Functions Start ---

    function emitPool_OutgoingTrancheClaimed(
        address claimer,
        uint256 trancheIndex,
        uint256 amountClaimed,
        address holdToken
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_OutgoingTrancheClaimed(
            msg.sender,
            claimer,
            trancheIndex,
            amountClaimed,
            holdToken
        );
    }

    function emitPool_OutgoingClaimSummary(
        uint256 totalClaimedAmount,
        uint256 outgoingTranchesBalance
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_OutgoingClaimSummary(
            msg.sender, 
            totalClaimedAmount,
            outgoingTranchesBalance
        );
    }

    function emitPool_IncomingTrancheUpdate(
        address caller,
        address owner,
        uint256 trancheIndex,
        uint256 amountAppliedToTranche,
        bool isNowComplete,
        bool wasOnTime,
        address holdToken
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_IncomingTrancheUpdate(
            msg.sender,
            caller,
            owner,
            trancheIndex,
            amountAppliedToTranche,
            isNowComplete,
            wasOnTime,
            holdToken
        );
    }

    function emitPool_IncomingReturnSummary(
        uint256 totalAmountAppliedInCall,
        uint256 totalAppliedToBonusInCall,
        uint256 lastCompletedIncomingTranche
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_IncomingReturnSummary(
            msg.sender,
            totalAmountAppliedInCall,
            totalAppliedToBonusInCall,
            lastCompletedIncomingTranche
        );
    }

    function emitPool_BonusWithdrawn(
        uint256 bonusAmountWithdrawn,
        uint256 eligibleRwaAmount
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_BonusWithdrawn(
            msg.sender,
            bonusAmountWithdrawn,
            eligibleRwaAmount
        );
    }

    function emitPool_FundsFullyReturned(
        address caller,
        address owner,
        uint256 timestamp
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_FundsFullyReturned(msg.sender, caller, owner, timestamp);
    }

    function emitPool_RwaMinted(
        address minter,
        uint256 rwaAmountMinted,
        uint256 holdAmountPaid,
        uint256 feePaid,
        uint256 percentBefore,
        uint256 userPercent,
        bool targetReached,
        string memory businessId,
        string memory poolId,
        address holdToken
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_RwaMinted(
            msg.sender,
            minter,
            rwaAmountMinted,
            holdAmountPaid,
            feePaid,
            percentBefore,
            userPercent,
            targetReached,
            businessId,
            poolId,
            holdToken
        );
    }

    function emitPool_TargetReached(
        uint256 outgoingTranchesBalance,
        uint256 floatingTimestampOffset,
        address owner
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_TargetReached(
            msg.sender,
            outgoingTranchesBalance,
            floatingTimestampOffset,
            owner
        );
    }

    function emitPool_RwaBurned(
        address burner,
        uint256 rwaAmountBurned,
        uint256 holdAmountReceived,
        uint256 bonusAmountReceived,
        uint256 holdFeePaid,
        uint256 bonusFeePaid,
        uint256 percentBefore,
        uint256 userPercent,
        bool targetReached,
        string memory businessId,
        string memory poolId,
        address holdToken
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_RwaBurned(
            msg.sender,
            burner,
            rwaAmountBurned,
            holdAmountReceived,
            bonusAmountReceived,
            holdFeePaid,
            bonusFeePaid,
            percentBefore,
            userPercent,
            targetReached,
            businessId,
            poolId,
            holdToken
        );
    }

    function emitPool_AwaitingRwaAmountUpdated(
        uint256 awaitingRwaAmount
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_AwaitingRwaAmountUpdated(msg.sender, awaitingRwaAmount); 
    }

    function emitPool_ReservesUpdated(
        uint256 realHoldReserve,
        uint256 virtualHoldReserve,
        uint256 virtualRwaReserve
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_ReservesUpdated(
            msg.sender, 
            realHoldReserve,
            virtualHoldReserve,
            virtualRwaReserve
        );
    }

    function emitPool_PausedStateChanged(
        bool isPaused
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_PausedStateChanged(msg.sender, isPaused); 
    }

    // Renamed from Pool_StaticConfigured to Pool_Deployed as per user's previous feedback and file state
    function emitPool_Deployed(
        address deployer,
        bool awaitCompletionExpired,
        bool floatingOutTranchesTimestamps,
        address holdToken,
        address rwaToken,
        uint256 tokenId,
        string memory entityId,
        string memory entityOwnerId,
        string memory entityOwnerType,
        address owner,
        uint256 expectedHoldAmount,
        uint256 expectedRwaAmount,
        uint256 expectedBonusAmount,
        uint256 rewardPercent,
        bool fixedSell,
        bool allowEntryBurn,
        uint256 entryPeriodStart,
        uint256 entryPeriodExpired,
        uint256 completionPeriodExpired,
        uint256 k,
        uint256 entryFeePercent,
        uint256 exitFeePercent,
        uint256[] memory outgoingTranches,
        uint256[] memory outgoingTranchTimestamps,
        uint256[] memory incomingTranches,
        uint256[] memory incomingTrancheExpired
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Pool_Deployed(
            msg.sender,
            deployer,
            awaitCompletionExpired,
            floatingOutTranchesTimestamps,
            holdToken,
            rwaToken,
            tokenId,
            entityId,
            entityOwnerId,
            entityOwnerType,
            owner,
            expectedHoldAmount,
            expectedRwaAmount,
            expectedBonusAmount,
            rewardPercent,
            fixedSell,
            allowEntryBurn,
            entryPeriodStart,
            entryPeriodExpired,
            completionPeriodExpired,
            k,
            entryFeePercent,
            exitFeePercent,
            outgoingTranches,
            outgoingTranchTimestamps,
            incomingTranches,
            incomingTrancheExpired
        );
    }

    // --- Pool Events End ---

    // --- DAO Events Start ---

    event Governance_ProposalCreated(
        address indexed emittedFrom,
        uint256 indexed proposalId,
        address indexed proposer,
        address target,
        bytes data,
        string description,
        uint256 startTime,
        uint256 endTime
    );

    event Governance_VoteCast(
        address indexed emittedFrom,
        uint256 indexed proposalId,
        address indexed voter,
        bool support,
        uint256 weight,
        string reason
    );

    event Governance_ProposalExecuted(
        address indexed emittedFrom,
        uint256 indexed proposalId,
        address indexed executor
    );

    event Governance_ProposalCancelled(
        address indexed emittedFrom,
        uint256 indexed proposalId,
        address indexed canceller
    );

    event DaoStaking_TokensStaked(
        address indexed emittedFrom,
        address indexed staker,
        uint256 amount,
        uint256 newVotingPower
    );

    event DaoStaking_TokensUnstaked(
        address indexed emittedFrom,
        address indexed staker,
        uint256 amount,
        uint256 newVotingPower
    );

    event Timelock_TransactionQueued(
        address indexed emittedFrom,
        bytes32 indexed txHash,
        address target,
        bytes data,
        uint256 eta
    );

    event Timelock_TransactionExecuted(
        address indexed emittedFrom,
        bytes32 indexed txHash,
        address target,
        bytes data,
        uint256 eta
    );

    event Timelock_TransactionCancelled(
        address indexed emittedFrom,
        bytes32 indexed txHash,
        address target,
        bytes data,
        uint256 eta
    );

    event Treasury_Withdrawal(
        address indexed emittedFrom,
        address indexed to,
        address indexed token,
        uint256 amount
    );

    // --- DAO Emitter Functions Start ---

    function emitGovernance_ProposalCreated(
        uint256 proposalId,
        address proposer,
        address target,
        bytes memory data,
        string memory description,
        uint256 startTime,
        uint256 endTime
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Governance_ProposalCreated(
            msg.sender,
            proposalId,
            proposer,
            target,
            data,
            description,
            startTime,
            endTime
        );
    }

    function emitGovernance_VoteCast(
        uint256 proposalId,
        address voter,
        bool support,
        uint256 weight,
        string memory reason
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Governance_VoteCast(
            msg.sender,
            proposalId,
            voter,
            support,
            weight,
            reason
        );
    }

    function emitGovernance_ProposalExecuted(
        uint256 proposalId,
        address executor
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Governance_ProposalExecuted(msg.sender, proposalId, executor);
    }

    function emitGovernance_ProposalCancelled(
        uint256 proposalId,
        address canceller
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Governance_ProposalCancelled(msg.sender, proposalId, canceller);
    }

    function emitDaoStaking_TokensStaked(
        address staker,
        uint256 amount,
        uint256 newVotingPower
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit DaoStaking_TokensStaked(msg.sender, staker, amount, newVotingPower);
    }

    function emitDaoStaking_TokensUnstaked(
        address staker,
        uint256 amount,
        uint256 newVotingPower
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit DaoStaking_TokensUnstaked(msg.sender, staker, amount, newVotingPower);
    }

    function emitTimelock_TransactionQueued(
        bytes32 txHash,
        address target,
        bytes memory data,
        uint256 eta
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Timelock_TransactionQueued(msg.sender, txHash, target, data, eta);
    }

    function emitTimelock_TransactionExecuted(
        bytes32 txHash,
        address target,
        bytes memory data,
        uint256 eta
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Timelock_TransactionExecuted(msg.sender, txHash, target, data, eta);
    }

    function emitTimelock_TransactionCancelled(
        bytes32 txHash,
        address target,
        bytes memory data,
        uint256 eta
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Timelock_TransactionCancelled(msg.sender, txHash, target, data, eta);
    }

    function emitTreasury_Withdrawal(
        address to,
        address token,
        uint256 amount
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Treasury_Withdrawal(msg.sender, to, token, amount);
    }

    // --- DAO Events End ---

    // --- ReferralTreasury Events Start ---

    event ReferralTreasury_Withdrawn(
        address indexed emittedFrom,
        address indexed user,
        address indexed token,
        uint256 amount
    );

    event ReferralTreasury_EmergencyWithdrawn(
        address indexed emittedFrom,
        address indexed to,
        address indexed token,
        uint256 amount,
        address caller
    );

    // --- ReferralTreasury Emitter Functions Start ---

    function emitReferralTreasury_Withdrawn(
        address user,
        address token,
        uint256 amount
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit ReferralTreasury_Withdrawn(msg.sender, user, token, amount);
    }

    function emitReferralTreasury_EmergencyWithdrawn(
        address to,
        address token,
        uint256 amount,
        address caller
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit ReferralTreasury_EmergencyWithdrawn(msg.sender, to, token, amount, caller);
    }

    // --- ReferralTreasury Events End ---

    // --- Factory Events Start ---

    event Factory_CreateRWAFeeCollected(
        address indexed emittedFrom,
        address indexed sender,
        uint256 amount,
        address token
    );

    event Factory_CreatePoolFeeCollected(
        address indexed emittedFrom,
        address indexed sender,
        uint256 amount,
        address token
    );

    // --- Factory Emitter Functions Start ---

    function emitFactory_CreateRWAFeeCollected(
        address sender,
        uint256 amount,
        address token
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Factory_CreateRWAFeeCollected(msg.sender, sender, amount, token);
    }

    function emitFactory_CreatePoolFeeCollected(
        address sender,
        uint256 amount,
        address token
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit Factory_CreatePoolFeeCollected(msg.sender, sender, amount, token);
    }

    // --- Factory Events End ---


    function uniqueContractId() public pure override returns (bytes32) {
        return keccak256("EventEmitter");
    }

    function implementationVersion() public pure override returns (uint256) {
        return 1;
    }

    function _verifyAuthorizeUpgradeRole() internal view override {
        addressBook.requireTimelock(msg.sender);
    }


    event RWA_PausedStateChanged(
        address indexed emittedFrom,
        bool isPaused
    );

    event RWA_Transfer(
        address indexed emittedFrom, 
        address indexed from,
        address indexed to,
        uint256 tokenId,
        uint256 amount,
        address pool
    );

    event RWA_Deployed(
        address indexed emittedFrom,
        address indexed deployer,
        address owner,
        string entityId
    );

    function emitRWA_Transfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 amount,
        address pool
    ) external {
        addressBook.requireProtocolContract(msg.sender); // Ensure caller is a registered RWA contract
        emit RWA_Transfer(msg.sender, from, to, tokenId, amount, pool);
    }


    function emitRWA_PausedStateChanged(
        bool isPaused
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit RWA_PausedStateChanged(msg.sender, isPaused);
    }

    function emitRWA_Deployed(
        address deployer,
        address owner,
        string memory entityId
    ) external {
        addressBook.requireProtocolContract(msg.sender);
        emit RWA_Deployed(msg.sender, deployer, owner, entityId);
    }
}
","keccak256":"0x0b438b112f433529eb474a8b9398dcaeee9cf9d679a0db26cd7c9a331495ccad","license":"MIT"},"contracts/utils/UpgradeableContract.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { ERC165Upgradeable } from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";

interface IUniqueVersionedContract {
    function uniqueContractId() external view returns (bytes32);
    function implementationVersion() external view returns (uint256);
}

abstract contract UpgradeableContract is
    IUniqueVersionedContract,
    UUPSUpgradeable,
    ERC165Upgradeable
{
    function upgradeToAndCall(
        address newImplementation,
        bytes memory data
    ) public payable virtual override onlyProxy {
        require(data.length > 0, "UpgradeableContract: empty upgrade data");
        super.upgradeToAndCall(newImplementation, data);
    }

    constructor() {
        _disableInitializers();
    }

    function __UpgradeableContract_init() internal onlyInitializing {
        __UUPSUpgradeable_init();
        __ERC165_init();
    }

    function __UpgradeableContract_init_unchained(address _addressBook) internal onlyInitializing {}

    function uniqueContractId() public view virtual returns (bytes32);
    function implementationVersion() public view virtual returns (uint256);

    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IUniqueVersionedContract).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _verifyAuthorizeUpgradeRole() internal view virtual;

    function _authorizeUpgrade(address newImplementation) internal view override {
        _verifyAuthorizeUpgradeRole();
        require(
            ERC165Upgradeable(newImplementation).supportsInterface(
                type(IUniqueVersionedContract).interfaceId
            ),
            "UpgradeableContract: new impl not IUniqueVersionedContract"
        );
        require(
            IUniqueVersionedContract(newImplementation).uniqueContractId() == uniqueContractId(),
            "UpgradeableContract: uniqueContractId not equals"
        );
        uint256 newVersion = IUniqueVersionedContract(newImplementation).implementationVersion();
        uint256 currentVersion = implementationVersion();
        require(
            newVersion > currentVersion && newVersion <= currentVersion + 100,
            "UpgradeableContract: invalid version upgrade"
        );
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;
}
